import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";

import Button from "@material-ui/core/Button";
import FlightIcon from "@material-ui/icons/FlightTakeoff";
import TextField from "@material-ui/core/TextField";

import Zoom from "@material-ui/core/Zoom";

const styles = theme => ({
  container: {
    flexWrap: "wrap",
    width: "100%",
    align: "center",
    float: "none",
    margin: "0 auto"
  },
  button: {
    "margin-right": "5%",
    "margin-bottom": "2%"
  }
});

export let buttonSupport;

class ReasonChoose extends React.Component {
  constructor() {
    super();
    this.state = {
      name: "",
      buttonClick: ""
    };
    this.sendDataToParent = this.sendDataToParent.bind(this);
  }

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value
    });
  };
  clickedVerspaetung = () => {
    buttonSupport = (
      <div className="buttonSupport">
        <span>Wie viele Stunden warst du zu spät?</span>
        <TextField
          type="number"
          id=""
          label="Stunden"
          className=""
          value={this.state.name}
          onChange={this.handleChange("name")}
        />
      </div>
    );
    this.forceUpdate();
  };

  clickedAufhebung = () => {
    buttonSupport = (
      <div className="buttonSupport">
        <p>Wie viele Stunden</p>
      </div>
    );
    this.forceUpdate();
  };
  clickedVerweigerung = () => {
    buttonSupport = <div className="buttonSupport">Verweigerung</div>;
    this.forceUpdate();
  };
  clickedAlk = () => {
    buttonSupport = <div className="buttonSupport">Algaui</div>;
    this.forceUpdate();
  };

  sendDataToParent(params) {
    //console.log(params);
    // console.log(e);
  }

  render() {
    const { classes } = this.props;
    console.log(this.props);
    return (
      <div>
        <form className={`${classes.container}`} noValidate autoComplete="off">
          <div className={"chooser"}>
            <h2>Ihr Flug: {this.props.name}</h2>
            <h3>Was ist geschehen?</h3>
            <Zoom in={true} timeout={100}>
              <Button
                variant="contained"
                color="secondary"
                className={classes.button}
                onClick={this.clickedVerspaetung}
                //onClick={send.sendFirstDataToServer.bind(this, this.state)}
                //onClick={this.sendDataToParent.bind(this, this.state)}
              >
                {this.props.column}
                Verspätung
                <FlightIcon className={classes.rightIcon} />
              </Button>
            </Zoom>
            <Zoom in={true} timeout={200}>
              <Button
                variant="contained"
                color="secondary"
                className={classes.button}
                onClick={this.clickedAufhebung}
              >
                Aufhebung
                <FlightIcon className={classes.rightIcon} />
              </Button>
            </Zoom>
            <Zoom in={true} timeout={300}>
              <Button
                variant="contained"
                color="secondary"
                className={classes.button}
                onClick={this.clickedVerweigerung}
                //onClick={send.sendFirstDataToServer.bind(this, this.state)}
              >
                Verweigerung
                <FlightIcon className={classes.rightIcon} />
              </Button>
            </Zoom>
            <Zoom in={true} timeout={400}>
              <Button
                variant="contained"
                color="secondary"
                className={classes.button}
                onClick={this.clickedAlk}
                //onClick={send.sendFirstDataToServer.bind(this, this.state)}
              >
                Auf Alk
                <FlightIcon className={classes.rightIcon} />
              </Button>
            </Zoom>
          </div>
        </form>
        {buttonSupport}
      </div>
    );
  }
}

ReasonChoose.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(ReasonChoose);
