import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";

import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const styles = theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap",

    align: "center",
    float: "none",
    margin: "0 auto"
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200
  },
  dense: {
    marginTop: 19
  },
  menu: {
    width: 200
  },
  button: {
    margin: theme.spacing.unit
  },

  leftIcon: {
    marginRight: theme.spacing.unit
  },
  rightIcon: {
    marginLeft: theme.spacing.unit
  },
  iconSmall: {
    fontSize: 20
  }
});

export let details = "";

class TextFields extends React.Component {
  constructor() {
    super();
    this.state = {
      button: "",
      text: "",
      multiline: ""
    };
    this.sendDataToParent = this.sendDataToParent.bind(this);
  }

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value
    });
  };

  enterDetails() {
    details = (
      <div>
        <h6>Gib uns ein paar Details darüber, was passiert ist</h6>
        <TextField
          id="outlined-multiline-flexible"
          label="Multiline"
          multiline
          rowsMax="6"
          value={this.state.text}
          onChange={this.handleChange("text")}
          margin="normal"
          helperText="Gib uns ein paar Details darüber, was passiert ist"
          variant="outlined"
          placeholder="Hilfreiche Informationen können sein:<br />
          Wie kam es zu der Flugunregelmäßiogkeit?<br />
          Wer hat dich über die Unregelmäßigkeit informaiert?"
        />{" "}
      </div>
    );
    this.forceUpdate();
  }

  sendDataToParent(e, params) {}

  render() {
    const { classes } = this.props;

    return (
      <form className={`${classes.container}`} noValidate autoComplete="off">
        <div className={"chooser"}>
          <Button
            variant="outlined"
            color="secondary"
            fullWidth={true}
            className={classes.button}
            onClick={this.enterDetails}
          >
            Technischer Defekt
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            fullWidth={true}
            className={classes.button}
          >
            Wetter
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            fullWidth={true}
            className={classes.button}
          >
            Streik
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            fullWidth={true}
            className={classes.button}
          >
            Probleme mit der Crew
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            fullWidth={true}
            className={classes.button}
          >
            Weiß nicht
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            fullWidth={true}
            className={classes.button}
          >
            Sonstiges
          </Button>
        </div>
        {details}
      </form>
    );
  }
}

TextFields.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(TextFields);
